import React, { Component } from 'react';

import './App.css';

import getTodos from './API/getTodos'

// import des composants
import TodoList from './components/TodoList'
import TodoForm from './components/TodoForm'



class TodoItem extends Component {


    render(){

        var item = this.props.item

        var tag = item.tag ? (
            <div className={"tag " + item.tag}>
                {item.tag}
            </div>
        ) : null
        var important = item.important ? (
            <div className="important">
                ⚑
            </div>
        ) : null
        return(
            <li key={item.id} className={item.done ? 'item-done' : 'item-undone'} >


                <div className="item-left">
                    <span 
                    className={item.done ? 'radio-done' : 'radio-undone'} 
                    onClick={()=>this.props.handleDone(item.id)}>
                        &#9675;
                    </span>
                    {item.text}
                </div>
                <div className="item-right">
                    {important}
                    {tag}
                    <span 
                    className="remove"
                    onClick={()=>this.props.handleRemove(item.id)}>
                        ✕
                    </span>
                </div>
            </li>
        )
    }
}

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: null,

        }
    }

    componentDidMount(){

        getTodos((result)=>{

            this.setState({
                todos: result
            })
        })
    }

    handleSubmit(task){

        var text = task.text
        var tag = task.tag 
        var isImportant = task.isImportant 

        // on récupère le max id de la liste pour éviter des problèmes lors de la suppression puis de l'ajout d'un item. 
        // sans cela on recréé 
        var maxid = 0;
        this.state.todos.map(function(obj){     
            if (obj.id > maxid) maxid = obj.id;    
        });

        // on créé un nouvel object item
        var item = {
            id: maxid+1,
            done: false,
            text: text,
            tag: tag,
            important: isImportant
        }

        // on met à jour le tableau des todo
        this.setState(prevState => ({
            todos: [...prevState.todos, item]
        }))

    }

    handleDone(id){

        // on créé d'abord une copie du state car un state est censé etre immuable        
        var todos = this.state.todos
        console.log(todos)
        todos.find(item => item.id === id).done = !todos.find(item => item.id === id).done

        // on met à jour le tableau des todo
        this.setState({
            todos: todos
        });
    }

    handleRemove(id){

        // on créé d'abord une copie du state car un state est censé etre immuable
        var todos = this.state.todos
        var index = todos.findIndex(item => item.id === id)

        todos.splice(index, 1);

        // on met à jour le tableau des todo
        this.setState({

            todos: todos
        });
    }

    render() {

        const { todos } = this.state;
        var list = this.state.todos ? (
                <TodoList 
                todos={todos} 
                handleDone={(id)=>this.handleDone(id)}
                handleRemove={(id)=>this.handleRemove(id)}
                />
        ) :
        (
            <div className="loading">
                Chargement des taches
            </div>
        )
        return (
        <div className="App">
            <div className="wrapper">
                <img className="logo" src="./logo.png"/>
                <h1>Entrer une tache</h1>
                <TodoForm handleSubmit={(value)=>this.handleSubmit(value)}/>

                {list}

            </div>
        </div>
        );
    }
}

export default App;
