/*  API/getCourses.js
    Robin Marillia

    Recupere les todos depuis un site distant
*/

function getTodos(callback){

    // le json donné dans l'énoncé contient un virgule en trop qui empeche d'utiliser JSON.parse
    // j'ai donc uploadé une version sur mon site pour pouvoir tout de même implémenter l'API
    const data = {
        optPost: 'myAPI',
        message: 'We make a research of fetch'
    };

    fetch("https://marillia-design.com/todo.php",{
        method: 'POST',
        body: JSON.stringify(data)

    } 
      ).then(function(response) {
        console.log(response)
        return response.json()
      }).then(function (value) {

            callback(value)
      })


}



export default getTodos
