import React, { Component } from 'react';

import './App.css';



class TodoForm extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleChange(event){
        this.setState({value: event.target.value});

    }

    handleSubmit(event) {
        // on envoit l'information au parent
        this.props.handleSubmit(this.state.value)

        // on empeche le rechargement de la page
        event.preventDefault();
    }

    render(){
        return(
            <form onSubmit={this.handleSubmit}>
                <input
                type="text"
                placeholder="Add a new todo"
                value={this.state.value} 
                onChange={this.handleChange}
                />
                <button type="submit">Add</button>
            </form>
        )
    }
}

class TodoList extends Component {

    handleDoneList(id){

        // on renvoit au parent l'id de l'item coché
        this.props.handleDone(id)
    }

    render(){
        return(
            <ul>
                {
                // les props key et id peuvent paraitre redondantes mais 
                // sont importante pour séparer les special props et notre 
                // transfert d'information perso

                this.props.todos.map((item, index) => (

                    <TodoItem key={item.id} id={item.id} idDone={item.done} handleDone={(id)=>this.handleDoneList(id)}>
                        {item.text}
                    </TodoItem>

                ))
                }
            </ul>
        )
    }
}

class TodoItem extends Component {

    handleDoneItem(id){

        // on renvoit au parent l'id de l'item coché
        this.props.handleDone(id)
    }

    render(){

        var id = this.props.id

        return(
            <li key={this.props.id}>
                <span className={this.props.isDone ? 'done' : 'undone'} onClick={()=>this.handleDoneItem(id)}>&#9675;</span>
                {this.props.children}
                <span className="remove">X</span>
            </li>
        )
    }
}

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: [
                { id: 1, done: false, text: 'Add new todo with form' },
                { id: 2, done: false, text: 'Create Components : TodoForm, TodoList, TodoItem ...' },
                { id: 3, done: false, text: 'Mark done an item' },
                { id: 4, done: false, text: 'Remove an item' },
            ],
        }
    }

    handleSubmit(value){

        // on créé un nouvel object item
        var item = {
            id: this.state.todos.length+1,
            done: false,
            text: value
        }

        // on met à jour le tableau des todo
        this.setState(prevState => ({
            todos: [...prevState.todos, item]
        }))

    }

    handleDone(id){

        // on créé d'abord une copie du state car un state est censé etre immuable
        var todos = this.state.todos
        todos[id+1].done = !todos[id+1].done
        console.log(todos)
        // on met à jour le tableau des todo
        this.setState({

            todos: todos
        });
    }

    render() {

        const { todos } = this.state;

        return (
        <div className="App">
            <div className="wrapper">

                <TodoForm handleSubmit={(value)=>this.handleSubmit(value)}/>

                <TodoList todos={todos} handleDone={(id)=>this.handleDone(id)}/>

            </div>
        </div>
        );
    }
}

export default App;
