import React, { Component } from 'react';

import './App.css';



class TodoForm extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleChange(event){
        this.setState({value: event.target.value});

    }

    handleSubmit(event) {

        event.preventDefault();
    }

    render(){
        return(
            <form onSubmit={this.handleSubmit}>
                <input
                type="text"
                placeholder="Add a new todo"
                value={this.state.value} 
                onChange={this.handleChange}
                />
                <button type="submit" onClick={()=>this.props.handleSubmit(this.state.value)}>Add</button>
            </form>
        )
    }
}

class TodoItem extends Component {

    render(){
        return(
            <li key={this.props.key}>
                <span className={this.props.isDone ? 'done' : 'undone'}>&#9675;</span>
                {this.props.children}
                <span className="remove">X</span>
            </li>
        )
    }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [
        { id: 1, done: false, text: 'Add new todo with form' },
        { id: 2, done: false, text: 'Create Components : TodoForm, TodoList, TodoItem ...' },
        { id: 3, done: false, text: 'Mark done an item' },
        { id: 4, done: false, text: 'Remove an item' },
      ],
    }
  }

  handleSubmit(value){

        // on créé un nouvel object item
        var item = {
            id: this.state.todos.length+1,
            done: false,
            text: value
        }

        // on met à jour le tableau des todo
        this.setState(prevState => ({
            todos: [...prevState.todos, item]
        }))


  }

  render() {
    const { todos } = this.state;

    return (
      <div className="App">
        <div className="wrapper">

            <TodoForm handleSubmit={(value)=>this.handleSubmit(value)}/>
            <ul>
                {
                todos.map((item, index) => (
                        <TodoItem key={item.id} idDone={item.done}>{item.text}</TodoItem>

                ))
                }
            </ul>
        </div>
      </div>
    );
  }
}

export default App;
