import React, { Component } from 'react';

import './App.css';



class TodoForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: '',
            isOptionsOpen: false,
            tagSelection: null,
            isImportant: false
        };


    }

    handleChange(event){
        this.setState({value: event.target.value});

    }

    handleSubmit(event) {
        // on envoit le texte et les informations du tag au parent
        var task = {
            text: this.state.value,
            tag: this.state.tagSelection
        }
        this.props.handleSubmit(task)

        // on empeche le rechargement de la page
        event.preventDefault();
    }

    handleOptions(){
        // on affiche ou enleve la fenetre d'options 
        this.setState(prevState => ({
            isOptionsOpen: !prevState.isOptionsOpen
        }))
    }

    handleTagSelection(tag){
        // on affiche ou enleve la fenetre d'options 
        this.setState({
            tagSelection: tag
        })
    }

    handleMarkImportant(tag){
        // on affiche ou enleve la fenetre d'options 
        this.setState({
            isImportant: tag
        })

    }

    render(){
        // classe pour le toogle de la fenetre options
        var isOptionsOpen = this.state.isOptionsOpen ? null : "hide"

        // tableau des tags disponibles 
        var tagsArray = ['arts', 'life', 'productivity', 'study']
        var tags = tagsArray.map((tag)=>{

            var isTagSelected = this.state.tagSelection == tag ? "tag-focus" : null

            return(
                <span 
                className={isTagSelected+ " tag "+tag}
                onClick={()=>this.handleTagSelection(tag)}
                >
                    {tag}
                </span>
        )})

        console.log(this.state.isImportant)

        return(
            <form onSubmit={this.handleSubmit}>
                <div className="form-searchBar">


                    <input
                    type="text"
                    placeholder="Add a new todo"
                    value={this.state.value} 
                    onChange={this.handleChange}
                    />
                    <div 
                    className="more-options-button"
                    onClick={()=>this.handleOptions()}
                    >
                    Options ▼
                    </div>
                    <button type="submit">Add</button>

                </div>
                <div className={"form-options-container "+ isOptionsOpen }>
                    <div className="form-options">
                        <div className="form-options-tags">
                            <p>Choose a tag for your task</p>
                            <div>
                                {tags}
                            </div>
                        </div>
                        <div className="form-options-important">
                            <span 
                            className={this.state.isImportant ? 'important-focus' : null}
                            onClick={()=>this.handleMarkImportant()}>
                                Mark as Important ⚑
                            </span>
                        </div>

                    </div>
                </div>
            </form>
        )
    }
}

class TodoList extends Component {


    render(){
        return(
            <ul>
                {

                this.props.todos.map((item, index) => (

                    <TodoItem 
                    key={item.id} 
                    item={item}
                    handleDone={(id)=>this.props.handleDone(id)}
                    handleRemove={(id)=>this.props.handleRemove(id)}
                    />                    

                ))
                }
            </ul>
        )
    }
}

class TodoItem extends Component {


    render(){

        var item = this.props.item

        var tag = item.tag ? (
            <span className={"tag " + item.tag}>
                {item.tag}
            </span>
        ) : null
        var important = item.important ? (
            <span className="important">
                ⚑
            </span>
        ) : null
        return(
            <li key={item.id} className={item.done ? 'item-done' : 'item-undone'} >
                <span 
                className={item.done ? 'radio-done' : 'radio-undone'} 
                onClick={()=>this.props.handleDone(item.id)}>
                    &#9675;
                </span>

                {item.text}
                <div className="item-right">
                    {important}
                    {tag}
                    <span 
                    className="remove"
                    onClick={()=>this.props.handleRemove(item.id)}>
                        X
                    </span>
                </div>
            </li>
        )
    }
}

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: [
                { id: 1, done: false, text: 'Add new todo with form', important:true, tag:'arts'},
                { id: 2, done: false, text: 'Create Components : TodoForm TodoList, TodoItem ...', tag:null},
                { id: 3, done: false, text: 'Mark done an item', tag:'productivity'},
                { id: 4, done: false, text: 'Remove an item', tag:'study'},
            ],
        }
    }

    handleSubmit(task){

       var text = task.text
       var tag = task.tag 

        // on récupère le max id de la liste pour éviter des problèmes lors de la suppression puis de l'ajout d'un item. 
        // sans cela on recréé 
        var maxid = 0;
        this.state.todos.map(function(obj){     
            if (obj.id > maxid) maxid = obj.id;    
        });

        // on créé un nouvel object item
        var item = {
            id: maxid+1,
            done: false,
            text: text,
            tag: tag
        }

        // on met à jour le tableau des todo
        this.setState(prevState => ({
            todos: [...prevState.todos, item]
        }))

    }

    handleDone(id){

        // on créé d'abord une copie du state car un state est censé etre immuable        
        var todos = this.state.todos
        console.log(todos)
        todos.find(item => item.id === id).done = !todos.find(item => item.id === id).done

        // on met à jour le tableau des todo
        this.setState({

            todos: todos
        });
    }

    handleRemove(id){

        // on créé d'abord une copie du state car un state est censé etre immuable
        var todos = this.state.todos
        todos.splice(id-1, 1);

        // on met à jour le tableau des todo
        this.setState({

            todos: todos
        });
    }

    render() {

        const { todos } = this.state;

        return (
        <div className="App">
            <div className="wrapper">

                <TodoForm handleSubmit={(value)=>this.handleSubmit(value)}/>

                <TodoList 
                todos={todos} 
                handleDone={(id)=>this.handleDone(id)}
                handleRemove={(id)=>this.handleRemove(id)}
                />

            </div>
        </div>
        );
    }
}

export default App;
